package pers.platform.common.utils;

import java.io.FileOutputStream;
import java.io.IOException;
import java.io.OutputStream;

import org.apache.commons.codec.binary.Base64;

public class Base64Util {

    public static void main(String[] args) {
        String str = "";
        convert64StrToImg(str,
                "E:/workspace/pers-cc-platform/pers-cc-common-utils/src/test/java/image/123.jpg");
    }

    public static boolean convert64StrToImg(String img64Str,
            String imgFilePath) {// 对字节数组字符串进行Base64解码并生成图片
        if (img64Str == null) // 图像数据为空
            return false;
        OutputStream out = null;
        try {
            // Base64解码
            byte[] bytes = Base64.decodeBase64(img64Str);
            for (int i = 0; i < bytes.length; ++i) {
                if (bytes[i] < 0) {// 调整异常数据
                    bytes[i] += 256;
                }
            }
            // 生成jpeg图片
            out = new FileOutputStream(imgFilePath);
            out.write(bytes);
            return true;
        } catch (Exception e) {
            return false;
        } finally {
            try {
                out.flush();
                out.close();
            } catch (IOException e) {
                // TODO Auto-generated catch block
                e.printStackTrace();
            }

        }
    }

}
